mapper.update:
	UPDATE B_ATTD_NUMBER 
	   SET CREATED_DATE= SYSDATE,
		   TIMER_MIN= #{timer },
		   START_TIME = #{startTime }
	 WHERE ATTD_NO = #{attdNo }

mapper.select:
SELECT class_no
  FROM b_attd_number
 WHERE attd_no=#{attdNo }

SELECT attd_status 
  FROM b_attendance
 WHERE attd_no = #{attdNo} 
   AND user_no= #{userNo}

SELECT description 
  FROM b_attendance
 WHERE attd_no = #{attdNo} 
   AND user_no= #{userNo}

SELECT to_char(created_date, 'MMDD')
  FROM b_attd_number b
 WHERE attd_no = #{attdNo }

mapper.delete:
DELETE FROM b_attendance 
 WHERE user_no = #{userNo } 
   AND attd_no = #{attdNo }

mapper.update:
UPDATE B_ATTENDANCE 
   SET attd_status= #{attdStatus }
 WHERE user_no = #{userNo } 
   AND attd_no = #{attdNo }
