mapper.select:
SELECT user_name as userName
  FROM b_user
 WHERE user_no = #{userNo }

SELECT to_char(created_date, 'MMDD')
  FROM b_attd_number b
 WHERE attd_no = #{attdNo }

SELECT description 
  FROM b_attendance
 WHERE attd_no = #{attdNo} 
   AND user_no= #{userNo}

SELECT class_no
  FROM B_ATTD_NUMBER
 WHERE ATTD_NO=#{attdNo }

mapper.delete:
DELETE FROM b_attendance 
 WHERE user_no = #{userNo } 
   AND attd_no = #{attdNo }

mapper.update:
UPDATE B_ATTENDANCE 
   SET attd_status= #{attdStatus }
 WHERE user_no = #{userNo } 
   AND attd_no = #{attdNo }
