mapper.select:
SELECT  vote_content AS voteContent
  FROM  b_vote_content
 where vote_number = #{voteNumber }

SELECT vote_content AS voteContent
  FROM b_vote_Content
 where vote_number = #{voteNumber }

SELECT vote_content AS voteContent
  FROM b_vote_answer
 where vote_content =#{voteContent } and vote_number = #{voteNumber }

select vote_title
from  b_vote_title
where vote_number = #{voteNumber}
